mapper.update:
update setmeal
category_id = #{categoryId}, id = #{id}, image = #{image}, name = #{name}, price = #{price}, description = #{description}
where id = #{id}

mapper.select:
select * from setmeal

 and name like concat('%',#{name},'%') 
 and category_id = #{categoryId} 
 and status = #{status}

select * from setmeal

    and name like concat('%', #{name}, '%')
    and category_id = #{categoryId}
    and status = #{status}

order by create_time desc
